CREATE DATABASE csdc_chatbot;
USE csdc_chatbot;

CREATE TABLE conversations (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id VARCHAR(50),
    message TEXT,
    response TEXT,
    timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE requests (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id VARCHAR(50),
    nombre VARCHAR(150),
    correo VARCHAR(150),
    tipo_solicitud VARCHAR(150),
    detalle TEXT,
    timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

